import { useState } from "react"

/**
 * localStorage „Å®ÂêåÊúü„Åô„Çã„Ç´„Çπ„Çø„É†„Éï„ÉÉ„ÇØ
 * @param keyName localStorage „Å´‰øùÂ≠ò„Åô„Çã„Ç≠„ÉºÂêç
 * @param initVal ÂàùÊúüÂÄ§
 */
const useLocalStorage = (keyName: string, initVal: string) => {

    // localStorage Áî®„ÅÆ„Ç≠„Éº„Çí‰ΩúÊàê
    const key = "hook:" + keyName;

    /**
     * localStorage „Åã„ÇâÂàùÊúüÂÄ§„ÇíÂèñÂæó„Åô„ÇãÈñ¢Êï∞
     * useState „ÅÆ„ÄåÈÅÖÂª∂ÂàùÊúüÂåñ„Äç„Åß‰Ωø„ÅÜ
     */
    const getInitialValue = () => {
        try {
            const item = localStorage.getItem(key)
            // ÂÄ§„Åå„ÅÇ„Çå„Å∞ JSON „Çí„Éë„Éº„Çπ„ÄÅ„Å™„Åë„Çå„Å∞ÂàùÊúüÂÄ§
            return item ? JSON.parse(item) : initVal
        } catch (error) {
            return initVal
        }
    }

    // üîΩ ÂàùÂõû„É¨„É≥„ÉÄ„É™„É≥„Ç∞ÊôÇ„Å†„Åë getInitialValue „ÅåÂÆüË°å„Åï„Çå„Çã
    const [savedValue, setSavedValue] = useState<string>(getInitialValue)

    /**
     * state „Å® localStorage „ÇíÂêåÊôÇ„Å´Êõ¥Êñ∞„Åô„ÇãÈñ¢Êï∞
     */
    const setValue = (val: string) => {
        try {
            setSavedValue(val) // state Êõ¥Êñ∞ ‚Üí ÂÜç„É¨„É≥„ÉÄ„É™„É≥„Ç∞
            localStorage.setItem(key, JSON.stringify(val)) // Ê∞∏Á∂öÂåñ
        } catch (error) {
            console.log(error)
        }
    }

    // useState „Å®Âêå„Åò„Ç§„É≥„Çø„Éº„Éï„Çß„Éº„Çπ„ÅßËøî„Åô
    return [savedValue, setValue] as const
}

const Hook5 = () => {
    const [text, setText] = useLocalStorage('LSData', '')

    return (
        <div>
            <input
                type="text"
                value={text}
                onChange={(e) => setText(e.target.value)}
            />
            <p>‰øùÂ≠ò„Åï„Çå„ÅüÂÄ§Ôºö{text}</p>
        </div>
    )
}
export default Hook5
