import { useState } from "react"

// ① カスタムフック（同じファイル内）
const useCounter = () => {
  const [count, setCount] = useState(0)
  const test = () => {
    return setCount(count+1) 
  }

  return(
    <div>{count}
    <button onClick={test}>カウントアップ</button>
    </div>
  )
}



export default useCounter

