import '../css/P01PropsList.css'

// Props3にアロー関数で、userListを代入
const Props3 = () => {
    const userList = [
        {id:1,name:"村人A"},
        {id:2,name:"村人B"},
        {id:3,name:"街人A"},
        {id:4,name:"街人B"},
        {id:5,name:"街人C"}
    ];
/* console.log(Props3);
結果：オブジェクトの配列が格納されている。
() => {
  const userList = [{
    id: 1,
    name: "村人A"
  }, {
    id: 2,
    name: "村人B"
  }, {
    id: 3,
    name: "街人A"
  }, {
    id: 4,
    name: "街人B"
  }, {
    id: 5,
    name: "街人C"
  }];

*/


// returnで、userListの配列から引数userに配列データを渡し、
// UserElementのプロパティとして、UserID、UserNameを渡している。
    return (
        <div class="display-area">
            {userList.map((user) =>  // Map関数により、配列の各要素を順番に取り出して、指定した処理を行い、新しい配列を作る。
                <UserElement 
                    UserID={user.id} // user.id を UserID という名前の props として渡す
                    UserName={user.name} // user.name を UserName という名前の props として渡す
                />
            )}
        </div>
    )
}

const UserElement = (props: {UserID:number, UserName:string}) =>{
    // 分割代入で、UserID,UserName に分けていれることができる。
    const {UserID,UserName} = props
    

    return <div> ID:{UserID} Name:{UserName}</div>
}

export default Props3
