import { useEffect, useState } from "react"

export interface Personal{
    name:string
    age:number
    mail:string
}

const Record = (props: {personal: Personal}) => {
    // プロップの取得
    const personal = props.personal

    // ステートフックの準備
    const [age,setAge] = useState(personal.age)
    const [msg,setMsg] = useState('')

    // 年齢加算ボタンのAction
    const doAddAge = () => {
        setAge(age+1)
    }

    useEffect(()=> {
        if(age >= 65){
            setMsg('定年です。')
        }
    },[age])

    useEffect(() => {
        
    },[msg])

    return (
        <tr>
            <td>{personal.name}</td>
            <td>{age}</td>
            <td>{personal.mail}</td>
            <td><button onClick={doAddAge}>年齢加算</button></td>
            <td>{msg}</td>
        </tr>
    )

}

export default Record
