/*リデューサーで以下のアクションを追加
カウンターに以下３つ
加算
減算
リセット
*/

import { useReducer } from "react"


// 状態管理用の変数
type ButtonState = number

// アクションの型定義
type TodoAction = {buttunName:'ADD_BUTTUN',number:number} | {buttunName:'minus_BUTTUN',number:number} | {buttunName:'delete_BUTTUN',number:number} 


// リデューサー関数
const ButtonReducer = (state:ButtonState , action:TodoAction) => {
    switch (action.buttunName) {
        case 'ADD_BUTTUN':
            return  (state + 1)

        case 'minus_BUTTUN':
            return  (state - 1)
            
        case 'delete_BUTTUN':
            return  (state = 0)

        default:
           return state
    }
}


const Counter02 = ( ) => {
 // useReducerの使用   useReducer(リデューサー関数, 初期値)
    const [todos, dispatch] = useReducer(ButtonReducer, 0)



    return (
        <div>
            カウンター：{todos}
            <input type="button" value="加算" onClick={() => dispatch({buttunName:'ADD_BUTTUN',number:1})} />
            <input type="button" value="減算" onClick={() => dispatch({buttunName:'minus_BUTTUN',number:1})} />
            <input type="button" value="リセット" onClick={() => dispatch({buttunName:'delete_BUTTUN',number:0})} />
            
        </div>
    )
}

export default Counter02
