import { useState } from "react"

interface Personal{
    name:string
    age:number
    mail:string
}

const Record = (props: {Personal: Personal}) => {
    const Personal = props.Personal

    // ステートフックの準備
    const [age,setAge] = useState(Personal.age) 
    const [msg,setMsg] = useState('')


    // 年齢加算ボタンのAction
    const doAddAge = ()=>{
        setAge(age=>age+1)
    }


    return(
        <tr>
            <td>{Personal.name}</td>
            <td>{age}</td>
            <td>{Personal.mail}</td>
            <td><button >年齢加算</button></td>
            <td>msg</td>
        </tr>

    )

}



export default Record
