import { useState } from "react"

const useCounter = () => {
    // useStateの中身を分割代入し、countに初期値0をセット
  const [count, setCount] = useState(0)
    console.log(count)
    
  // 関数plusCountを作成し、PlusCountがclickされた際に、setCount関数内で引数countに+1する。
  const plusCount = () => {
    setCount(count+ 1)
  }

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={plusCount}>カウントアップ</button>
    </div>
  )
}

export default useCounter
