import { useState } from "react"

const Counter01 = () => {
    const [count,setValue] = useState(0);
    
    const test = () => {
     return   setValue(count => count+1)
    }
    console.log(count)
    console.log(test)
  

    return (
    <div>
        カウンター：0
        <input type="button" value="加算" />
    </div>
    )
}

export default Counter01

