// counterボタンを押すと1づつ増える。
import { useState } from "react"

const Counter01 = () => {
    const [value,setValue] = useState('a')

    console.log(value)
    console.log(setValue)

    setValue(1)

    return (
    <div>
        カウンター：
        <input type="button" value="加算" />
    </div>
    )
}

export default Counter01

