import { useState } from "react"

const useCounter = () => {
  const [count, setCount] = useState(0)
    console.log(count)
    console.log(setCount)
  
  const plusCount = () => {
    setCount(count+ 1)
  }

  return {
    count,
    plusCount,
  }
}

const Counter03 = () => {
  const { count, plusCount } = useCounter()

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={plusCount}>カウントアップ</button>
    </div>
  )
}

export default Counter03
