// Todo型の宣言
interface Todo{
    id: number,
    text: string
    completed: boolean
}

// 状態管理配列
type TodoState = Todo[]

// リデューサーはセットなんたらを関数のように使える
// TodoAction のaddtodoは追加する  payloadはonかオフ
// TOGGLE_TODO 完了か非完了か　
// DELETE_TODO 削除するかどうか


type TodoAction ={type:'ADD_TODO', payload:string} | {type:'TOGGLE_TODO', payload:number} | {type:'DELETE_TODO', payload:number}

// リデューサーロジック
// 状態遷移のロジック ：今何をしているのか状態を表す設定
const todoReducer = (state:TodoState,action:TodoAction ) => {
    switch (action.type) {
        // メモを追加する
        // ... state は配列の一番後ろに要素を追加したい場合にこのような書き方がある。
        case 'ADD_TODO':
        return    [
                ...state,{
                    id: Date.now(),
                    text: action.payload,
                    completed: false
                }
            ]
        case 'TOGGLE_TODO':
            return state.map((todo) => {
                if(todo.id === action.payload){
                    return {...todo,completed:!todo.completed}
                }
                return todo
            })
        case 'DELETE_TODO':
            return state.filter((todo) => {
                return todo.id !== action.payload
            })
        default:
            return state
    }
}



